import React, { useState, useEffect } from "react";
import { fetchTransactions } from "./api/index";
import FileUpload from "./components/FileUpload";
import { Pie, Line } from "react-chartjs-2";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import "./App.css";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function App() {
  const [transactions, setTransactions] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);

  const loadData = async () => {
    try {
      const transactionsData = await fetchTransactions();
      // Process transactions...
      setTransactions(transactionsData.data);
      setDataLoaded(true);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    loadData(); // Fetch transactions on initial load
  }, []);

  const calculateSummary = () => {
    let totalIncome = 0;
    let totalExpense = 0;
    transactions.forEach((tx) => {
      if (tx.Amount > 0) {
        totalIncome += tx.Amount;
      } else {
        totalExpense += Math.abs(tx.Amount);
      }
    });
    return { totalIncome, totalExpense };
  };

  const generatePieChartData = () => {
    const categoryTotals = {};
    transactions.forEach((tx) => {
      const category = tx.Category || tx.category || "Uncategorized";
      categoryTotals[category] =
        (categoryTotals[category] || 0) + Math.abs(tx.Amount || 0);
    });
    return {
      labels: Object.keys(categoryTotals),
      datasets: [
        {
          label: "Transaction Categories",
          data: Object.values(categoryTotals),
          backgroundColor: [
            "#FF6384", "#36A2EB", "#FFCE56", "#4CAF50", "#FF5733", "#8e44ad",
          ],
          hoverOffset: 4,
        },
      ],
    };
  };

  const generateLineChartData = () => {
    // Sorting and data processing for line chart
    const sortedTx = [...transactions].sort((a, b) => new Date(a.transaction_date || a.Date) - new Date(b.transaction_date || b.Date));
    let balance = 0;
    const labels = sortedTx.map((tx) => new Date(tx.transaction_date || tx.Date).toLocaleDateString());
    const data = sortedTx.map((tx) => {
      balance += tx.Amount || 0;
      return balance;
    });
    return {
      labels,
      datasets: [
        {
          label: "Net Account Balance",
          data,
          fill: false,
          borderColor: "rgb(75, 192, 192)",
          tension: 0.1,
        },
      ],
    };
  };

  const summaryData = calculateSummary();

  return (
    <Router>
      <div className="app-container">
        <h1 className="app-title">Mini Bank Statement Analyzer</h1>
        <FileUpload onUploadSuccess={loadData} />

        <div className="tabs">
          <Link to="/transactions">
            <button>Transactions</button>
          </Link>
          <Link to="/summary">
            <button>Summary</button>
          </Link>
          <Link to="/chart">
            <button>Chart</button>
          </Link>
          <Link to="/lineChart">
            <button>Line Graph</button>
          </Link>
        </div>

        <Switch>
          <Route path="/transactions">
            {dataLoaded && (
              <section className="transaction-details">
                <h2>Transactions</h2>
                <table className="transaction-table">
                  <thead>
                    <tr>
                      {transactions.length > 0 &&
                        Object.keys(transactions[0]).map((key) => (
                          <th key={key}>{key}</th>
                        ))}
                    </tr>
                  </thead>
                  <tbody>
                    {transactions.map((tx) => (
                      <tr key={tx.id}>
                        {Object.keys(tx).map((key) => (
                          <td key={key}>{tx[key]}</td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </section>
            )}
          </Route>

          <Route path="/summary">
            {dataLoaded && (
              <section className="summary-details">
                <h2>Summary</h2>
                <div>
                  <p>Total Income: {summaryData.totalIncome}</p>
                  <p>Total Expense: {summaryData.totalExpense}</p>
                </div>
              </section>
            )}
          </Route>

          <Route path="/chart">
            {dataLoaded && (
              <section className="chart-details">
                <h2>Category Pie Chart</h2>
                <div className="chart-wrapper">
                  <Pie data={generatePieChartData()} options={{ maintainAspectRatio: false }} />
                </div>
              </section>
            )}
          </Route>

          <Route path="/lineChart">
            {dataLoaded && (
              <section className="line-chart-details">
                <h2>Net Account Balance Line Graph</h2>
                <div className="chart-wrapper">
                  <Line data={generateLineChartData()} options={{ maintainAspectRatio: false }} />
                </div>
              </section>
            )}
          </Route>

          <Route path="/">
            <h2>Select a tab above to view content.</h2>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
