const express = require("express");
const fs = require("fs");
const path = require("path");

const app = express();
const PORT = 5000;

// Clear transactions API
app.post("/clear-transactions", (req, res) => {
  try {
    const transactionsFilePath = path.join(__dirname, "uploads", "transactions.json");
    if (fs.existsSync(transactionsFilePath)) {
      fs.writeFileSync(transactionsFilePath, JSON.stringify([])); // Empty JSON file
    }
    res.status(200).json({ message: "Transactions cleared successfully" });
  } catch (error) {
    res.status(500).json({ message: "Error clearing transactions", error });
  }
});

app.listen(PORT, () => console.log(`Server running on http://localhost:${PORT}`));
