import React, { useState } from "react";
import { fetchTransactions } from "./api/index";
import FileUpload from "./components/FileUpload";
import { Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import "./App.css";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Tabs = ({ activeTab, onTabChange }) => {
  const tabs = ["transactions", "summary", "chart", "lineChart"];
  return (
    <div className="tabs">
      {tabs.map(tab => (
        <button
          key={tab}
          onClick={() => onTabChange(tab)}
          className={activeTab === tab ? "active" : ""}
        >
          {tab.charAt(0).toUpperCase() + tab.slice(1)}
        </button>
      ))}
    </div>
  );
};

const TransactionTable = ({ transactions }) => {
  return (
    <table className="transaction-table">
      <thead>
        <tr>
          {transactions.length > 0 &&
            Object.keys(transactions[0]).map(key => (
              <th key={key}>{key}</th>
            ))}
        </tr>
      </thead>
      <tbody>
        {transactions.map(tx => (
          <tr key={tx.id}>
            {Object.keys(tx).map(key => (
              <td key={key} className={tx.Amount > 0 ? "credit" : "debit"}>
                {tx[key]}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

const Summary = ({ summaryData }) => (
  <section className="summary-details">
    <h2>Summary</h2>
    <div>
      <p>Total Income: {summaryData.totalIncome}</p>
      <p>Total Expense: {summaryData.totalExpense}</p>
    </div>
  </section>
);

const App = () => {
  const [transactions, setTransactions] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [activeTab, setActiveTab] = useState("transactions");

  const loadData = async () => {
    try {
      const transactionsData = await fetchTransactions();
      const processed = transactionsData.data.map((tx) => {
        if (tx.deposit !== undefined && tx.withdrawal !== undefined) {
          const deposit = parseFloat(tx.deposit) || 0;
          const withdrawal = parseFloat(tx.withdrawal) || 0;
          return { ...tx, Amount: deposit - withdrawal };
        }
        if (tx.Amount !== undefined) {
          return { ...tx, Amount: parseFloat(tx.Amount) || 0 };
        }
        return tx;
      });
      setTransactions(processed);
      setDataLoaded(true);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const calculateSummary = () => {
    let totalIncome = 0;
    let totalExpense = 0;
    transactions.forEach((tx) => {
      if (tx.Amount > 0) {
        totalIncome += tx.Amount;
      } else {
        totalExpense += Math.abs(tx.Amount);
      }
    });
    return { totalIncome, totalExpense };
  };

  const generatePieChartData = () => {
    const categoryTotals = {};
    transactions.forEach((tx) => {
      const category = tx.Category || tx.category || "Uncategorized";
      categoryTotals[category] =
        (categoryTotals[category] || 0) + Math.abs(tx.Amount || 0);
    });
    const labels = Object.keys(categoryTotals);
    const data = Object.values(categoryTotals);
    return {
      labels,
      datasets: [
        {
          label: "Transaction Categories",
          data,
          backgroundColor: [
            "#7f8c8d",
            "#95a5a6",
            "#bdc3c7",
            "#34495e",
            "#2ecc71",
            "#3498db",
          ],
          hoverOffset: 4,
        },
      ],
    };
  };

  const generateLineChartData = () => {
    const sortedTx = [...transactions].sort((a, b) => {
      const dA = new Date(a.transaction_date || a.Date);
      const dB = new Date(b.transaction_date || b.Date);
      return dA - dB;
    });

    const labels = [];
    const data = [];
    let balance = 0;
    sortedTx.forEach((tx) => {
      const date = new Date(tx.transaction_date || tx.Date);
      labels.push(date.toLocaleDateString());
      balance += tx.Amount || 0;
      data.push(balance);
    });

    return {
      labels,
      datasets: [
        {
          label: "Net Account Balance",
          data,
          fill: false,
          borderColor: "#3498db",
          tension: 0.1,
        },
      ],
    };
  };

  const summaryData = calculateSummary();

  return (
    <div className="app-container">
      <h1 className="app-title">Mini Bank Statement Analyzer</h1>
      <FileUpload onUploadSuccess={loadData} />

      <Tabs activeTab={activeTab} onTabChange={setActiveTab} />

      {dataLoaded && (
        <div className="content-container">
          {activeTab === "transactions" && (
            <section className="transaction-details card">
              <h2>Transactions</h2>
              <TransactionTable transactions={transactions} />
            </section>
          )}

          {activeTab === "summary" && (
            <Summary summaryData={summaryData} />
          )}

          {activeTab === "chart" && (
            <section className="chart-details card">
              <h2>Category Pie Chart</h2>
              <div className="chart-wrapper">
                <Pie data={generatePieChartData()} options={{ maintainAspectRatio: false }} />
              </div>
            </section>
          )}

          {activeTab === "lineChart" && (
            <section className="line-chart-details card">
              <h2>Net Account Balance Line Graph</h2>
              <div className="chart-wrapper">
                <Line data={generateLineChartData()} options={{ maintainAspectRatio: false }} />
              </div>
            </section>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
