import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import { fetchTransactions } from "./api/index";
import FileUpload from "./components/FileUpload";
import { Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import "./App.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function Transactions({ transactions }) {
  return (
    <section className="transaction-details">
      <h2>Transactions</h2>
      <table className="transaction-table">
        <thead>
          <tr>
            {transactions.length > 0 &&
              Object.keys(transactions[0]).map((key) => <th key={key}>{key}</th>)}
          </tr>
        </thead>
        <tbody>
          {transactions.map((tx, index) => (
            <tr key={index}>
              {Object.keys(tx).map((key) => (
                <td key={key}>{tx[key]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </section>
  );
}

function Summary({ transactions }) {
  const totalIncome = transactions.reduce((acc, tx) => (tx.Amount > 0 ? acc + tx.Amount : acc), 0);
  const totalExpense = transactions.reduce((acc, tx) => (tx.Amount < 0 ? acc + Math.abs(tx.Amount) : acc), 0);

  return (
    <section className="summary-details">
      <h2>Summary</h2>
      <p>Total Income: {totalIncome}</p>
      <p>Total Expense: {totalExpense}</p>
    </section>
  );
}

function ChartView({ transactions }) {
  const categoryTotals = transactions.reduce((acc, tx) => {
    const category = tx.Category || tx.category || "Uncategorized";
    acc[category] = (acc[category] || 0) + Math.abs(tx.Amount || 0);
    return acc;
  }, {});

  return (
    <section className="chart-details">
      <h2>Category Pie Chart</h2>
      <div className="chart-wrapper">
        <Pie
          data={{
            labels: Object.keys(categoryTotals),
            datasets: [
              {
                data: Object.values(categoryTotals),
                backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4CAF50", "#FF5733", "#8e44ad"],
              },
            ],
          }}
        />
      </div>
    </section>
  );
}

function LineGraph({ transactions }) {
  const sortedTx = [...transactions].sort((a, b) => new Date(a.transaction_date || a.Date) - new Date(b.transaction_date || b.Date));
  let balance = 0;
  return (
    <section className="line-chart-details">
      <h2>Net Account Balance Line Graph</h2>
      <div className="chart-wrapper">
        <Line
          data={{
            labels: sortedTx.map(tx => new Date(tx.transaction_date || tx.Date).toLocaleDateString()),
            datasets: [{ label: "Net Balance", data: sortedTx.map(tx => (balance += tx.Amount || 0)), borderColor: "rgb(75, 192, 192)" }],
          }}
        />
      </div>
    </section>
  );
}

function App() {
  const [transactions, setTransactions] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const loadData = async () => {
      try {
        const transactionsData = await fetchTransactions();
        const processed = transactionsData.data.map((tx) => ({
          ...tx,
          Amount: parseFloat(tx.deposit || 0) - parseFloat(tx.withdrawal || 0) || parseFloat(tx.Amount) || 0,
        }));
        setTransactions(processed);
        setDataLoaded(true);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    loadData();
  }, []);

  return (
    <div className="app-container">
      <h1 className="app-title">Mini Bank Statement Analyzer</h1>
      <FileUpload onUploadSuccess={() => setDataLoaded(false)} />

      <nav className="tabs">
        <Link to="/transactions" className={location.pathname === "/transactions" ? "active" : ""}>
          Transactions
        </Link>
        <Link to="/summary" className={location.pathname === "/summary" ? "active" : ""}>
          Summary
        </Link>
        <Link to="/chart" className={location.pathname === "/chart" ? "active" : ""}>
          Chart
        </Link>
        <Link to="/lineChart" className={location.pathname === "/lineChart" ? "active" : ""}>
          Line Graph
        </Link>
      </nav>

      {dataLoaded && (
        <Routes>
          <Route path="/transactions" element={<Transactions transactions={transactions} />} />
          <Route path="/summary" element={<Summary transactions={transactions} />} />
          <Route path="/chart" element={<ChartView transactions={transactions} />} />
          <Route path="/lineChart" element={<LineGraph transactions={transactions} />} />
          <Route path="*" element={<Transactions transactions={transactions} />} />
        </Routes>
      )}
    </div>
  );
}

export default function WrappedApp() {
  return (
    <Router>
      <Routes>
        <Route path="/*" element={<App />} />
      </Routes>
    </Router>
  );
}
