import React, { useState, useEffect } from "react";
import { fetchTransactions } from "./api/index";
import FileUpload from "./components/FileUpload";
import { BrowserRouter as Router, Route, Link, Routes, useNavigate } from "react-router-dom";  // Import React Router
import { Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import "./App.css";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function App() {
  const [transactions, setTransactions] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [activeTab, setActiveTab] = useState("transactions"); // "transactions", "summary", "chart", "lineChart"


  const navigate = useNavigate();  // React Router hook to manage history

  useEffect(() => {
    // Update URL based on active tab
    navigate(`/${activeTab}`);
  }, [activeTab, navigate]);

  const loadData = async () => {
    try {
      const transactionsData = await fetchTransactions();
      // Process transactions: if deposit and withdrawal fields exist, compute Amount
      const processed = transactionsData.data.map((tx) => {
        if (tx.deposit !== undefined && tx.withdrawal !== undefined) {
          const deposit = parseFloat(tx.deposit) || 0;
          const withdrawal = parseFloat(tx.withdrawal) || 0;
          return { ...tx, Amount: deposit - withdrawal };
        }
        if (tx.Amount !== undefined) {
          return { ...tx, Amount: parseFloat(tx.Amount) || 0 };
        }
        return tx;
      });
      setTransactions(processed);
      setDataLoaded(true);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const calculateSummary = () => {
    let totalIncome = 0;
    let totalExpense = 0;
    transactions.forEach((tx) => {
      if (tx.Amount > 0) {
        totalIncome += tx.Amount;
      } else {
        totalExpense += Math.abs(tx.Amount);
      }
    });
    return { totalIncome, totalExpense };
  };

  const generatePieChartData = () => {
    const categoryTotals = {};
    transactions.forEach((tx) => {
      // Use either 'Category' or 'category' (default to Uncategorized)
      const category = tx.Category || tx.category || "Uncategorized";
      categoryTotals[category] =
        (categoryTotals[category] || 0) + Math.abs(tx.Amount || 0);
    });
    const labels = Object.keys(categoryTotals);
    const data = Object.values(categoryTotals);
    return {
      labels,
      datasets: [
        {
          label: "Transaction Categories",
          data,
          backgroundColor: [
            "#FF6384",
            "#36A2EB",
            "#FFCE56",
            "#4CAF50",
            "#FF5733",
            "#8e44ad",
          ],
          hoverOffset: 4,
        },
      ],
    };
  };

  const generateLineChartData = () => {
    // Sort transactions by date (using "transaction_date" or "Date")
    const sortedTx = [...transactions].sort((a, b) => {
      const dA = new Date(a.transaction_date || a.Date);
      const dB = new Date(b.transaction_date || b.Date);
      return dA - dB;
    });

    const labels = [];
    const data = [];
    let balance = 0;
    sortedTx.forEach((tx) => {
      const date = new Date(tx.transaction_date || tx.Date);
      labels.push(date.toLocaleDateString());
      balance += tx.Amount || 0;
      data.push(balance);
    });

    const summaryData = calculateSummary();

    return {
      labels,
      datasets: [
        {
          label: "Net Account Balance",
          data,
          fill: false,
          borderColor: "rgb(75, 192, 192)",
          tension: 0.1,
        },
      ],
    };
  };

  const summaryData = calculateSummary();

  return (
    <Router>
      <div className="app-container">
        <h1 className="app-title">Mini Bank Statement Analyzer</h1>
        <FileUpload onUploadSuccess={loadData} />

        {/* Navigation buttons with links */}
        <div className="tabs">
          <Link to="/transactions" className={activeTab === "transactions" ? "active" : ""}>
            Transactions
          </Link>
          <Link to="/summary" className={activeTab === "summary" ? "active" : ""}>
            Summary
          </Link>
          <Link to="/chart" className={activeTab === "chart" ? "active" : ""}>
            Chart
          </Link>
          <Link to="/lineChart" className={activeTab === "lineChart" ? "active" : ""}>
            Line Graph
          </Link>
        </div>

        <Routes>
          <Route path="/transactions">
            {dataLoaded && (
              <section className="transaction-details">
                <h2>Transactions</h2>
                {/* Render transactions table here */}
              </section>
            )}
          </Route>
          <Route path="/summary">
            {dataLoaded && (
              <section className="summary-details">
                <h2>Summary</h2>
                <p>Total Income: {summaryData.totalIncome}</p>
                <p>Total Expense: {summaryData.totalExpense}</p>
              </section>
            )}
          </Route>
          <Route path="/chart">
            {dataLoaded && (
              <section className="chart-details">
                <h2>Category Pie Chart</h2>
                <Pie data={generatePieChartData()} options={{ maintainAspectRatio: false }} />
              </section>
            )}
          </Route>
          <Route path="/lineChart">
            {dataLoaded && (
              <section className="line-chart-details">
                <h2>Net Account Balance Line Graph</h2>
                <Line data={generateLineChartData()} options={{ maintainAspectRatio: false }} />
              </section>
            )}
          </Route>
        </Routes>
      </div>
    </Router>
  );
}

export default App;
