import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import { fetchTransactions } from "./api/index";
import FileUpload from "./components/FileUpload";
import { Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import "./App.css";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function App() {
  const [transactions, setTransactions] = useState([]);
  const location = useLocation();
  
  // Load data based on the active tab (URL)
  const loadData = async () => {
    try {
      const transactionsData = await fetchTransactions();
      // Process transactions
      const processed = transactionsData.data.map((tx) => {
        if (tx.deposit !== undefined && tx.withdrawal !== undefined) {
          const deposit = parseFloat(tx.deposit) || 0;
          const withdrawal = parseFloat(tx.withdrawal) || 0;
          return { ...tx, Amount: deposit - withdrawal };
        }
        if (tx.Amount !== undefined) {
          return { ...tx, Amount: parseFloat(tx.Amount) || 0 };
        }
        return tx;
      });
      setTransactions(processed);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    loadData();
  }, [location.pathname]);

  const calculateSummary = () => {
    let totalIncome = 0;
    let totalExpense = 0;
    transactions.forEach((tx) => {
      if (tx.Amount > 0) {
        totalIncome += tx.Amount;
      } else {
        totalExpense += Math.abs(tx.Amount);
      }
    });
    return { totalIncome, totalExpense };
  };

  const generatePieChartData = () => {
    const categoryTotals = {};
    transactions.forEach((tx) => {
      const category = tx.Category || tx.category || "Uncategorized";
      categoryTotals[category] =
        (categoryTotals[category] || 0) + Math.abs(tx.Amount || 0);
    });
    const labels = Object.keys(categoryTotals);
    const data = Object.values(categoryTotals);
    return {
      labels,
      datasets: [
        {
          label: "Transaction Categories",
          data,
          backgroundColor: [
            "#FF6384",
            "#36A2EB",
            "#FFCE56",
            "#4CAF50",
            "#FF5733",
            "#8e44ad",
          ],
          hoverOffset: 4,
        },
      ],
    };
  };

  const generateLineChartData = () => {
    const sortedTx = [...transactions].sort((a, b) => {
      const dA = new Date(a.transaction_date || a.Date);
      const dB = new Date(b.transaction_date || b.Date);
      return dA - dB;
    });

    const labels = [];
    const data = [];
    let balance = 0;
    sortedTx.forEach((tx) => {
      const date = new Date(tx.transaction_date || tx.Date);
      labels.push(date.toLocaleDateString());
      balance += tx.Amount || 0;
      data.push(balance);
    });

    return {
      labels,
      datasets: [
        {
          label: "Net Account Balance",
          data,
          fill: false,
          borderColor: "rgb(75, 192, 192)",
          tension: 0.1,
        },
      ],
    };
  };

  const summaryData = calculateSummary();

  return (
    <Router>
      <div className="app-container">
        <h1 className="app-title">Mini Bank Statement Analyzer</h1>
        <FileUpload onUploadSuccess={loadData} />

        <div className="tabs">
          <Link to="/transactions">
            <button className={location.pathname === "/transactions" ? "active" : ""}>
              Transactions
            </button>
          </Link>
          <Link to="/summary">
            <button className={location.pathname === "/summary" ? "active" : ""}>
              Summary
            </button>
          </Link>
          <Link to="/chart">
            <button className={location.pathname === "/chart" ? "active" : ""}>
              Chart
            </button>
          </Link>
          <Link to="/lineChart">
            <button className={location.pathname === "/lineChart" ? "active" : ""}>
              Line Graph
            </button>
          </Link>
        </div>

        <Routes>
          <Route path="/transactions" element={
            <section className="transaction-details">
              <h2>Transactions</h2>
              <table className="transaction-table">
                <thead>
                  <tr>
                    {transactions.length > 0 &&
                      Object.keys(transactions[0]).map((key) => (
                        <th key={key}>{key}</th>
                      ))}
                  </tr>
                </thead>
                <tbody>
                  {transactions.map((tx) => (
                    <tr key={tx.id}>
                      {Object.keys(tx).map((key) => (
                        <td key={key}>{tx[key]}</td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </section>
          } />
          <Route path="/summary" element={
            <section className="summary-details">
              <h2>Summary</h2>
              <div>
                <p>Total Income: {summaryData.totalIncome}</p>
                <p>Total Expense: {summaryData.totalExpense}</p>
              </div>
            </section>
          } />
          <Route path="/chart" element={
            <section className="chart-details">
              <h2>Category Pie Chart</h2>
              <div className="chart-wrapper">
                <Pie data={generatePieChartData()} options={{ maintainAspectRatio: false }} />
              </div>
            </section>
          } />
          <Route path="/lineChart" element={
            <section className="line-chart-details">
              <h2>Net Account Balance Line Graph</h2>
              <div className="chart-wrapper">
                <Line key={location.pathname} data={generateLineChartData()} options={{ maintainAspectRatio: false }} />
              </div>
            </section>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
