import React, { useState } from "react";
import axios from "axios";

const FileUpload = ({ onUploadSuccess }) => {
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState("");

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file) {
            setMessage("Please select a file first.");
            return;
        }

        const formData = new FormData();
        formData.append("file", file);

        try {
            const response = await axios.post("http://localhost:5000/api/upload", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });
            setMessage(response.data.message);
            
            if (onUploadSuccess) {
              setTimeout(() => {
                onUploadSuccess(); // Fetch transactions after successful upload
            }, 2000);  // 2 seconds delay // Fetch transactions after successful upload
            }
        } catch (error) {
            setMessage(error.response?.data?.message || "File upload failed.");
        }
    };

    return (
        <div>
            <h2>Upload Bank Statement (CSV or JSON)</h2>
            <input type="file" onChange={handleFileChange} accept=".csv,.json" />
            <button onClick={handleUpload}>Upload</button>
            {message && <p>{message}</p>}
        </div>
    );
};

export default FileUpload;
