import React, { useState, useEffect } from "react";
import { fetchTransactions } from "./api/index";
import FileUpload from "./components/FileUpload";
import { Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import { BrowserRouter as Router, Route, Link, Routes, useLocation, useNavigate } from "react-router-dom";
import "./App.css";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function App() {
  const [transactions, setTransactions] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [activeTab, setActiveTab] = useState("transactions"); // "transactions", "summary", "chart", "lineChart"
  const location = useLocation();
  const navigate = useNavigate(); // useNavigate hook for programmatic navigation

  const loadData = async () => {
    try {
      const transactionsData = await fetchTransactions();
      const processed = transactionsData.data.map((tx) => {
        if (tx.deposit !== undefined && tx.withdrawal !== undefined) {
          const deposit = parseFloat(tx.deposit) || 0;
          const withdrawal = parseFloat(tx.withdrawal) || 0;
          return { ...tx, Amount: deposit - withdrawal };
        }
        if (tx.Amount !== undefined) {
          return { ...tx, Amount: parseFloat(tx.Amount) || 0 };
        }
        return tx;
      });
      setTransactions(processed);
      setDataLoaded(true);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const calculateSummary = () => {
    let totalIncome = 0;
    let totalExpense = 0;
    transactions.forEach((tx) => {
      if (tx.Amount > 0) {
        totalIncome += tx.Amount;
      } else {
        totalExpense += Math.abs(tx.Amount);
      }
    });
    return { totalIncome, totalExpense };
  };

  const generatePieChartData = () => {
    const categoryTotals = {};
    transactions.forEach((tx) => {
      const category = tx.Category || tx.category || "Uncategorized";
      categoryTotals[category] = (categoryTotals[category] || 0) + Math.abs(tx.Amount || 0);
    });
    const labels = Object.keys(categoryTotals);
    const data = Object.values(categoryTotals);
    return {
      labels,
      datasets: [
        {
          label: "Transaction Categories",
          data,
          backgroundColor: [
            "#FF6384",
            "#36A2EB",
            "#FFCE56",
            "#4CAF50",
            "#FF5733",
            "#8e44ad",
          ],
          hoverOffset: 4,
        },
      ],
    };
  };

  const generateLineChartData = () => {
    const sortedTx = [...transactions].sort((a, b) => {
      const dA = new Date(a.transaction_date || a.Date);
      const dB = new Date(b.transaction_date || b.Date);
      return dA - dB;
    });

    const labels = [];
    const data = [];
    let balance = 0;
    sortedTx.forEach((tx) => {
      const date = new Date(tx.transaction_date || tx.Date);
      labels.push(date.toLocaleDateString());
      balance += tx.Amount || 0;
      data.push(balance);
    });

    return {
      labels,
      datasets: [
        {
          label: "Net Account Balance",
          data,
          fill: false,
          borderColor: "rgb(75, 192, 192)",
          tension: 0.1,
        },
      ],
    };
  };

  const summaryData = calculateSummary();

  useEffect(() => {
    if (location.pathname === "/summary") {
      setActiveTab("summary");
    } else if (location.pathname === "/chart") {
      setActiveTab("chart");
    } else if (location.pathname === "/lineChart") {
      setActiveTab("lineChart");
    } else {
      setActiveTab("transactions");
    }
  }, [location]);

  return (
    <Router>
      <div className="app-container">
        <h1 className="app-title">Mini Bank Statement Analyzer</h1>
        <FileUpload onUploadSuccess={loadData} />

        <div className="tabs">
          <Link to="/transactions">
            <button
              onClick={() => setActiveTab("transactions")}
              className={activeTab === "transactions" ? "active" : ""}
            >
              Transactions
            </button>
          </Link>
          <Link to="/summary">
            <button
              onClick={() => {
                setActiveTab("summary");
                navigate("/summary"); // Using navigate
              }}
              className={activeTab === "summary" ? "active" : ""}
            >
              Summary
            </button>
          </Link>
          <Link to="/chart">
            <button
              onClick={() => setActiveTab("chart")}
              className={activeTab === "chart" ? "active" : ""}
            >
              Chart
            </button>
          </Link>
          <Link to="/lineChart">
            <button
              onClick={() => setActiveTab("lineChart")}
              className={activeTab === "lineChart" ? "active" : ""}
            >
              Line Graph
            </button>
          </Link>
        </div>

        {dataLoaded && (
          <div className="content-container">
            <Routes>
              <Route path="/transactions" element={<div>Transactions</div>} />
              <Route path="/summary" element={<div>Summary</div>} />
              <Route path="/chart" element={<div>Chart</div>} />
              <Route path="/lineChart" element={<div>Line Chart</div>} />
            </Routes>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
