import React, { useState, useEffect } from "react";
import { fetchTransactions } from "./api/index";
import FileUpload from "./components/FileUpload";
import { Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import "./App.css";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function App() {
  const [transactions, setTransactions] = useState([]);
  const [dataLoaded, setDataLoaded] = useState(false);
  const [activeTab, setActiveTab] = useState("transactions");

  // Fetch data whenever the active tab changes
  useEffect(() => {
    loadData();
  }, [activeTab]); // Run whenever the active tab changes

  const loadData = async () => {
    try {
      console.log(`Fetching data for endpoint: /transactions`);
      const transactionsData = await fetchTransactions();
      console.log("API Response:", transactionsData);

      // Process transactions
      const processed = transactionsData.data.map((tx) => {
        if (tx.deposit !== undefined && tx.withdrawal !== undefined) {
          const deposit = parseFloat(tx.deposit) || 0;
          const withdrawal = parseFloat(tx.withdrawal) || 0;
          return { ...tx, Amount: deposit - withdrawal };
        }
        if (tx.Amount !== undefined) {
          return { ...tx, Amount: parseFloat(tx.Amount) || 0 };
        }
        return tx;
      });

      setTransactions(processed);
      setDataLoaded(true);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="app-container">
      <h1 className="app-title">Mini Bank Statement Analyzer</h1>
      <FileUpload onUploadSuccess={loadData} />

      <div className="tabs">
        <button onClick={() => setActiveTab("transactions")} className={activeTab === "transactions" ? "active" : ""}>
          Transactions
        </button>
        <button onClick={() => setActiveTab("summary")} className={activeTab === "summary" ? "active" : ""}>
          Summary
        </button>
        <button onClick={() => setActiveTab("chart")} className={activeTab === "chart" ? "active" : ""}>
          Chart
        </button>
        <button onClick={() => setActiveTab("lineChart")} className={activeTab === "lineChart" ? "active" : ""}>
          Line Graph
        </button>
      </div>

      {dataLoaded && (
        <div className="content-container">
          {activeTab === "transactions" && (
            <section className="transaction-details">
              <h2>Transactions</h2>
              <table className="transaction-table">
                <thead>
                  <tr>
                    {transactions.length > 0 && Object.keys(transactions[0]).map((key) => <th key={key}>{key}</th>)}
                  </tr>
                </thead>
                <tbody>
                  {transactions.map((tx) => (
                    <tr key={tx.id}>
                      {Object.keys(tx).map((key) => (
                        <td key={key}>{tx[key]}</td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </section>
          )}

          {activeTab === "summary" && (
            <section className="summary-details">
              <h2>Summary</h2>
              <div>
                <p>Total Income: {/* display total income */}</p>
                <p>Total Expense: {/* display total expense */}</p>
              </div>
            </section>
          )}

          {activeTab === "chart" && (
            <section className="chart-details">
              <h2>Category Pie Chart</h2>
              <div className="chart-wrapper">
                <Pie data={generatePieChartData()} options={{ maintainAspectRatio: false }} />
              </div>
            </section>
          )}

          {activeTab === "lineChart" && (
            <section className="line-chart-details">
              <h2>Net Account Balance Line Graph</h2>
              <div className="chart-wrapper">
                <Line key={activeTab} data={generateLineChartData()} options={{ maintainAspectRatio: false }} />
              </div>
            </section>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
