import React, { useState } from "react";
import { fetchTransactions, fetchSummary } from "./api/index";
import FileUpload from './components/FileUpload'; 
import { Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';
import './App.css';  

// Register chart components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function App() {
    const [transactions, setTransactions] = useState([]);
    const [summary, setSummary] = useState([]);
    const [dataLoaded, setDataLoaded] = useState(false); 
    const [activeTab, setActiveTab] = useState("transactions"); // Default active tab

    const loadData = async () => {
        try {
            const transactionsData = await fetchTransactions();
            const summaryData = await fetchSummary();
            
            setTransactions(transactionsData.data);
            setSummary(summaryData.data);
            setDataLoaded(true); // Show data after fetching
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    const handleTabChange = (tab) => {
        setActiveTab(tab);
    };

    const categorizedTransactions = categorizeTransactions();
    const { totalCredits, totalDebits, net } = calculateSummary();

    const chartData = {
        labels: ['Income', 'Expenses'],
        datasets: [
            {
                label: 'Amount',
                data: [totalCredits, totalDebits],
                backgroundColor: ['#4CAF50', '#FF6347'],
                borderColor: ['#388E3C', '#D32F2F'],
                borderWidth: 1,
            },
        ],
    };

    const pieChartData = {
        labels: categoryData.map(item => item.category),
        datasets: [
            {
                data: categoryData.map(item => item.total),
                backgroundColor: ['#FF6347', '#4CAF50', '#FFD700', '#1E90FF'],
                borderColor: '#fff',
                borderWidth: 1,
            },
        ],
    };

    return (
        <div className="app-container">
            <h1 className="app-title">Mini Bank Statement Analyzer</h1>
            <FileUpload onUploadSuccess={loadData} />

            <div className="tabs">
                <button className={activeTab === "transactions" ? "active" : ""} onClick={() => handleTabChange("transactions")}>Transactions</button>
                <button className={activeTab === "summary" ? "active" : ""} onClick={() => handleTabChange("summary")}>Summary</button>
                <button className={activeTab === "upload" ? "active" : ""} onClick={() => handleTabChange("upload")}>File Upload</button>
            </div>

            {activeTab === "transactions" && dataLoaded && (
                <div className="content-container">
                    <section className="transaction-details">
                        <h2>Transactions</h2>
                        {Object.keys(categorizedTransactions).map((category) => (
                            <div key={category} className="category-section">
                                <h3>{category}</h3>
                                <ul className="transaction-list">
                                    {categorizedTransactions[category].map((t) => (
                                        <li key={t.id} className="transaction-item">
                                            {t.description}: ${t.amount}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        ))}
                    </section>
                </div>
            )}

            {activeTab === "summary" && dataLoaded && (
                <div className="content-container">
                    <section className="summary">
                        <h2>Summary</h2>
                        <ul className="summary-list">
                            <li>Total Credits: ${totalCredits}</li>
                            <li>Total Debits: ${totalDebits}</li>
                            <li>Net: ${net}</li>
                        </ul>
                    </section>

                    <section className="charts">
                        <h2>Expense vs. Income Visualization</h2>
                        <div className="chart-container">
                            <Bar data={chartData} />
                        </div>

                        <h2>Transaction Categories Breakdown</h2>
                        <div className="chart-container">
                            <Pie data={pieChartData} />
                        </div>
                    </section>
                </div>
            )}

            {activeTab === "upload" && (
                <div className="upload-container">
                    <h2>Upload Bank Statement</h2>
                    <FileUpload onUploadSuccess={loadData} />
                </div>
            )}
        </div>
    );
}

export default App;
